mapper.update:
UPDATE bill_task
SET dateline = #{dateline}
WHERE bill_id = #{billId}
  AND user_code = #{userCode}
  AND is_deleted = 0

mapper.select:
select count(id)
from bill_task
where bill_id = #{billId}
  and task_id = #{taskId}
  AND is_deleted = 0
  and id != #{id}
  AND node_status = #{status}

select count(id)
from bill_task
where bill_id = #{billId}
  AND is_deleted = 0
  and task_id = #{taskId}
  AND task_type = #{type}
